mapper.select:
SELECT ncontent
FROM notice
WHERE nno=#{nno}

SELECT mb.mb_no, mb.mb_title, mb.mb_read, mb.mb_del, mb.mb_board, mb.m_no, m.m_name, s.b_catename, m.m_id, DATE_FORMAT(mb.mb_date,"%Y-%m-%d") as mb_date, (SELECT COUNT(*) FROM multiboard mb, members m
AND mb.m_no=m.m_no AND mb_board = #{cate}
AND mb_title LIKE CONCAT('%', #{searchV}, '%') AND mb_content LIKE CONCAT('%', #{searchV}, '%') AND m_name LIKE CONCAT('%', #{searchV}, '%') AND m_id LIKE CONCAT('%', #{searchV}, '%')
) AS count FROM multiboard mb JOIN members m ON mb.m_no = m.m_no JOIN setupboard s ON mb.mb_board = s.mb_cate
AND mb_board = #{cate}
AND mb_title LIKE CONCAT('%', #{searchV}, '%') AND mb_content LIKE CONCAT('%', #{searchV}, '%') AND m_name LIKE CONCAT('%', #{searchV}, '%') AND m_id LIKE CONCAT('%', #{searchV}, '%')
ORDER BY mb.mb_no DESC

SELECT mb_content
FROM multiboard
WHERE mb_no = #{mbno}
